import struct import socket x       = 4y       = 8z		= 22.33bytesToSend         = struct.pack('hhf', x, y, z)serverAddressPort   = ("127.0.0.1", 20001)bufferSize          = 1024 # Create a UDP socket at client sideUDPClientSocket = socket.socket(family=socket.AF_INET, type=socket.SOCK_DGRAM)# Send to server using created UDP socketUDPClientSocket.sendto(bytesToSend, serverAddressPort)